mapper.select:
SELECT a.i_reser, a.i_host, a.i_user,a.check_in AS chin,a.check_out AS chout, a.gest_qty AS qty , a.total_fee,a.reser_state, b.img_url, c.ROOM_TITLE,c.addr,DATE_FORMAT(a.R_DT,'%Y-%m-%d') AS R_DT, DATE_FORMAT(a.m_dt,'%Y-%m-%d') AS M_DT FROM user_reser a INNER JOIN ( SELECT * FROM rooms_img GROUP BY i_host ) b ON a.i_host = b.i_host INNER JOIN host_user c ON a.i_host = c.I_HOST WHERE c.I_USER = #{i_user}
AND a.check_out < DATE_FORMAT(NOW(),'%Y-%m-%d')
AND ISNULL(a.reser_state) ORDER BY a.R_DT DESC

mapper.update:
UPDATE user_reser a
INNER JOIN user_rsv_change b
ON a.I_RESER = b.I_RESER
SET a.reser_state = NULL,
a.check_in	= b.BE_CHIN,
a.check_out = b.BE_CHOUT,
a.GEST_QTY 	= b.BE_GEST_QTY,
a.total_FEE	= b.BE_TOTAL_FEE
WHERE a.i_reser = #{i_reser}
AND a.reser_state = 'ch';

UPDATE host_user a
INNER JOIN user_rsv_change b
ON a.i_host = b.i_host
SET a.RV_STATE = (a.RV_STATE-b.gest_qty)+ b.BE_GEST_QTY 
WHERE b.i_reser = #{i_reser}
AND b.STATE IS NULL;

UPDATE user_rsv_change
SET state = 'o'
WHERE i_reser = #{i_reser}
AND state IS NULL;

mapper.select:
SELECT EXISTS(
	SELECT  *
	FROM host_user a
	INNER JOIN user_rsv_change b
	ON a.i_host = b.i_host
	WHERE b.i_reser = #{i_reser}
	AND a.R_VASTION >= (a.RV_STATE-b.gest_qty)+ b.BE_GEST_QTY 
	AND b.STATE IS NULL
);

mapper.delete:
DELETE FROM aircnc_msg 
WHERE i_reser = #{i_reser};

DELETE a
FROM aircnc_msgusers a
INNER JOIN aircnc_msglist b
ON a.i_mlist = b.i_mlist
WHERE b.I_RESER = #{i_reser};

DELETE FROM aircnc_msglist
WHERE i_reser = #{i_reser};

mapper.select:
SELECT a.I_HOST, a.i_user, a.COUNTRY, a.ADDR, a.b_room, a.BED_QTY, a.BTH_ROOM, a.ROOM_TITLE, f.img_url ,DATE_FORMAT(a.R_DT,'%Y-%m-%d') as R_DT ,DATE_FORMAT(a.M_DT,'%Y-%m-%d') as M_DT, if(b.I_HOST is null or c.I_HOST is null or d.i_host is null or e.I_HOST is null or f.i_host is NULL or g.I_HOST is null or h.i_host is null or i.i_host is null or j.i_host is null or k.i_host is null or l.I_HOST is null or m.i_host is NULL,'x','o') AS state FROM host_user a LEFT JOIN build_type b ON a.i_host = b.i_host LEFT JOIN FACILITE c ON a.i_host = c.i_host LEFT JOIN SAFE_PLANT d ON a.i_host = d.i_host LEFT JOIN GEST_SPACE e ON a.i_host = e.i_host LEFT JOIN ( SELECT * FROM rooms_img GROUP BY i_host ) f ON a.i_host = f.i_host LEFT JOIN SPACE_INFO g ON a.i_host = g.i_host LEFT JOIN room_rule h ON a.i_host = h.i_host LEFT JOIN ( SELECT * FROM dtl_rule GROUP BY i_host ) i ON a.i_host = i.i_host LEFT JOIN room_dt_rule j ON a.i_host = j.i_host LEFT JOIN CHECK_INOUT k ON a.i_host = k.i_host LEFT JOIN ROOM_FEE l ON a.i_host = l.i_host LEFT JOIN Workplace_ph m ON a.i_host = m.i_host WHERE a.i_user = #{i_user}
AND a.room_title LIKE '%${room_title}%'
ORDER BY a.M_DT desc;

mapper.delete:
