mapper.select:
SELECT a.I_RESER, a.i_host, a.i_user, a.check_in AS chin, a.check_out AS chout, a.GEST_QTY AS qty, a.total_FEE, b.ROOM_TITLE, b.ADDR, c.CHECKIN_T, c.FINICH_T, d.IMG_URL, IFNULL(e.I_CONTENTS,0) as review_state FROM user_reser a INNER JOIN host_user b ON a.i_host = b.i_host INNER JOIN check_inout c ON a.I_HOST = c.I_HOST INNER JOIN ( SELECT * FROM rooms_img GROUP BY i_host ) d ON a.i_host = d.i_host LEFT JOIN host_review e ON a.I_RESER = e.i_reser AND a.I_HOST = e.I_HOST AND a.I_USER = e.I_USER WHERE a.i_user = #{i_user}
AND a.check_out < DATE_FORMAT(NOW(),'%Y-%m-%d')
AND (a.reser_state = 'o' OR a.reser_state IS null) ORDER BY a.R_DT ASC, IFNULL(e.I_CONTENTS,0) asc
